import React, { useRef, useEffect } from 'react';
import { ColorPicker } from './ColorPicker';
import { useColor } from './ColorContext'; // Usa il context per cambiare il colore
import { darken } from 'polished';
import '../Css/MyComponent.css';

export function Footer() {
    const footerRef = useRef(null);
    const { color } = useColor(); // Ottieni il colore dal context

    useEffect(() => {
        if (footerRef.current) {
            const darkenedColor = darken(0.1, color); // Oscura il colore
            footerRef.current.style.backgroundColor = darkenedColor; // Applica il colore scuro
        }
    }, [color]); // Si aggiorna quando `color` cambia

    return (
        <footer className='lower-bar' ref={footerRef}>
            <ColorPicker /> {/* Il ColorPicker Ã¨ usato per cambiare il colore */}
        </footer>
    );
}
