import React, { useEffect, useState, useRef } from 'react';
import { Header } from './Components/Header';
import { MainContainer } from './Components/MyComponent';
import { Footer } from './Components/Footer';
import { ColorProvider, useColor } from './Components/ColorContext';
import '../src/Css/MyComponent.css';

function App() {

  const appRef = useRef(null);
  const { color } = useColor(); // Ottieni il colore dal contesto

  useEffect(() => {
    if (appRef.current) {
      appRef.current.style.backgroundColor = color;
    }
  }, [color]);

  return (
    <div className="App" ref={appRef}>
      <ColorProvider>
        <Header />
        <MainContainer />
        <Footer />
      </ColorProvider>
    </div>
  );
}

export default App;
