import React, { useState, useRef, useEffect } from 'react';
import { ColorPicker } from './ColorPicker';
import { darken } from 'polished';
import '../Css/MyComponent.css';

export function Footer() {
    const footerRef = useRef(null)
    const initialColor = localStorage.getItem('color') || '#ffffff';
    const [footerColor, setFooterColor] = useState(initialColor);

    const handleColorChange = (newColor) => {
        setFooterColor(newColor);
        localStorage.setItem('footerColor', newColor);
    }

    useEffect(() => {
        if (footerRef.current) {
            const darkenedColor = darken(0.1, newColor);
            footerRef.current.style.backgroundColor = darkenedColor
        }
    })



    return (
        <footer className='lower-bar' ref={footerRef}>
            <ColorPicker onColorChange={handleColorChange} />
        </footer>
    );
}
