// src/MyComponent.jsx
import React, { useEffect, useState } from 'react';
import '../Css/MyComponent.css';

export function Header() {
  return (
    <header className='upper-bar'>
      <h1 className='title'>Memo</h1>
    </header>
  );
}

export function MainContainer() {
  // useState -> Stato iniziale dell'array (vuoto)
  const [notes, setNotes] = useState(() => {
    const savedNotes = localStorage.getItem('notes');
    //Vera -> parsing delle note | falsa -> array vuoto
    return savedNotes ? JSON.parse(savedNotes) : [];
  });

  // Funzione per aggiungere una nuova nota
  const addNewNote = () => {
    const newNote = { id: Date.now(), title: 'Titolo', content: 'Scrivi qui' };
    setNotes([...notes, newNote]);
  };

  // Funzione per aggiornare una nota
  const updateNote = (id, newTitle, newContent) => {
    const updatedNotes = notes.map(note =>
      note.id === id ? { ...note, title: newTitle, content: newContent } : note
    );
    setNotes(updatedNotes);
  };

  const deleteNote = (id) => {
    //Crea l'array con le note diverse da quella che voglio cancellare
    setNotes(notes.filter(note => note.id !== id));
  }

  useEffect(() => {
    localStorage.setItem('notes', JSON.stringify(notes));
  }, [notes]); //useEffect esegue la funzione ogni volta che 'notes' cambia stato

  return (
    <div className='container' id='container'>
      <div className='post-it-area' id='post-it-area'>
        {notes.map((note) => (
          <div key={note.id} className='post-it'>
            <button className='delete-btn' onClick={() => deleteNote(note.id)}>x</button>
            <h2 className='post-it-title' contentEditable="true" onInput={(e) => updateNote(note.id, e.target.textContent, note.content)} aria-valuemax={note.title}></h2>
            <textarea className='post-it-content' defaultValue={note.content}></textarea>
          </div>
        ))}
        <div className='add-container'>
          <div className='add-post-it'>
            <div className='div-btn'>
              <button className='add-btn' onClick={addNewNote}></button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}


export function Footer() {
  return (
    <footer className='lower-bar'>
      <button className='bg-change-btn'>Cambia colore dello sfondo</button>
    </footer>
  );
}
