// src/MyComponent.jsx
import React from 'react';
import '../Css/MyComponent.css';

export function Header() {
  return (
    <header className='upper-bar'>
      <h1 className='title'>Memo</h1>
    </header>
  );
}

export function MainContainer() {

  const addNewNote = () => {
    const container = document.getElementById('post-it-area')
    const post_it = document.createElement('div');
    const post_it_title = document.createElement('h1');
    const post_it_content = document.createElement('textarea');

    post_it_title.textContent = "Titolo"
    post_it_content.textContent = "Scrivi qui"

    post_it.className = "post-it";
    post_it_title.className = "post-it-title";
    post_it_content.className = "post-it-content";

    post_it.append(post_it_title, post_it_content);
    container.appendChild(post_it)
  }


  return (
    <div className='container' id='container'>
      <div className='post-it-area' id='post-it-area'></div>
      <div className='add-post-it'>
        <div className='div-btn'>
          <button className='add-btn' onClick={addNewNote}></button>
        </div>
      </div>
    </div>
  )
}

export function Footer() {
  return (
    <footer className='lower-bar'>
      <button className='bg-change-btn'>Cambia colore dello sfondo</button>
    </footer>
  );
}
