import React, { useEffect, useState, useRef } from 'react';
import { useColor } from './ColorProvider';
import { darken } from 'polished';
import '../Css/MyComponent.css';

export function Header() {
    const headerRef = useRef(null)
    const initialColor = localStorage.getItem('color') || '#ffffff';
    const [headerColor, setHeaderColor] = useState(initialColor);

    const handleColorChange = (newColor) => {
        setHeaderColor(newColor);
        localStorage.setItem('headerColor', newColor);
    }

    useEffect(() => {
        if (headerRef.current) {
            const darkenedColor = darken(0.1, initialColor);
            headerRef.current.style.backgroundColor = darkenedColor
        }
    }, [headerColor]);
    return (
        <header className='upper-bar' ref={headerRef} onChange={handleColorChange}>
            <h1 className='title'>Memo</h1>
        </header>
    );
}
