// src/MyComponent.jsx
import React, { useEffect, useState, useRef } from 'react';
import '../Css/MyComponent.css';
import '../Css/ColorPicker.css'

export function Header() {
  return (
    <header className='upper-bar'>
      <h1 className='title'>Memo</h1>
    </header>
  );
}

export function MainContainer() {
  // useState -> Stato iniziale dell'array (vuoto)
  const [notes, setNotes] = useState(() => {
    const savedNotes = localStorage.getItem('notes');
    //Vera -> parsing delle note | falsa -> array vuoto
    return savedNotes ? JSON.parse(savedNotes) : [];
  });

  // Funzione per aggiungere una nuova nota
  const addNewNote = () => {
    const newNote = { id: Date.now(), title: 'Titolo', content: 'Scrivi qui' };
    setNotes([...notes, newNote]);
  };

  // Funzione per aggiornare una nota
  const updateNote = (id, newTitle, newContent) => {
    const updatedNotes = notes.map(note =>
      note.id === id ? { ...note, title: newTitle, content: newContent } : note
    );
    setNotes(updatedNotes);
  };

  const deleteNote = (id) => {
    //Crea l'array con le note diverse da quella che voglio cancellare
    setNotes(notes.filter(note => note.id !== id));
  }

  useEffect(() => {
    localStorage.setItem('notes', JSON.stringify(notes));
  }, [notes]); //useEffect esegue la funzione ogni volta che 'notes' cambia stato

  return (
    <div className='container' id='container'>
      <div className='post-it-area' id='post-it-area'>
        {notes.map((note) => (
          <div key={note.id} className='post-it'>
            <button className='delete-btn' onClick={() => deleteNote(note.id)}>x</button>
            <input className='post-it-title' value={note.title} onInput={(e) => updateNote(note.id, e.target.value, note.content)}></input>
            <textarea className='post-it-content' defaultValue={note.content} onInput={(e) => updateNote(note.id, note.title, e.target.value)}></textarea>
          </div>
        ))}
        <div className='add-container'>
          <div className='add-post-it'>
            <div className='div-btn'>
              <button className='add-btn' onClick={addNewNote}></button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}


export function Footer() {

  return (
    <footer className='lower-bar'>
      <ColorPicker />
    </footer>
  );
}

export function ColorPicker() {
  const elementRef = useRef(null);
  const initialColor = localStorage.getItem('color');
  const [color, setColor] = useState(initialColor);

  const changeColor = (e) => {
    const newColor = e.target.value;
    setColor(newColor);
    localStorage.setItem('color', newColor);
  }

  const getColor = () => {
    if (elementRef.current) {
      const computedStyle = window.getComputedStyle(elementRef.current);
      const color = computedStyle.backgroundColor; // Ottieni il colore del testo
      alert("Il colore del testo è: " + color);
    }
  };

  useEffect(() => {
    if (elementRef.current) {
      elementRef.current.style.backgroundColor = color;
    }
  }, [color]);

  return (
    <div className='color-picker-container'>
      <button ref={elementRef} className='bg-change-btn' onClick={getColor}>Cambia colore dello sfondo</button>
      <input type="color" id="favcolor" className='color-input' value={color}></input>
    </div>
  );
}
